import React, { useState } from "react";

import axios from 'axios';

export default function Form(props) {

  const [form, setForm] = useState({});

  const handleEmailChange = e => {
    setForm(
      {
      [e.target.name]: e.target.value,
      [e.target.img]: e.target.value,
      waterInterval: 21600,
      lastWatered: new Date()
      })
  }

  const handleSubmit = e => {
    e.preventDefault()
    axios.post("/api/plant", [{
      [e.target.name]: e.target.value,
      [e.target.img]: e.target.value,
      waterInterval: 21600,
      lastWatered: new Date()
      }])
    .then((res) => {props.setPlants([...props.plants])})
  };

  return (
    <seciton className="form">
      <form
        className="form-inline"
        onSubmit={handleSubmit}
        >
        <div className="form-group mb-2">
          <label for="staticEmail2" class="sr-only">
            Image Adress
          </label>
          <input
            type="text"
            name="img"
            value={props.plants.img}
            onChange={handleEmailChange}
            class="form-control"
            id="text"
            placeholder="Image Adress"
          />
        </div>
        <div className="form-group mx-sm-3 mb-2">
          <label for="inputPassword2" class="sr-only">
            Plant Name
          </label>
          <input
            type="text"
            name="name"
            value={props.plants.name}
            onChange={handleEmailChange}
            class="form-control"
            id="inputPassword2"
            placeholder="Plant Name"
          />
        </div>
        <button type="submit" class="btn btn-dark mb-2" >
          Add Plant
        </button>
      </form>
    </seciton>
  );
}
