import React, { useState } from "react";

export default function Form(props) {

  const handleSubmit = async() => {
    // store the states in the form data
    const loginFormData = new FormData();
    loginFormData.append("img", props.plant.img)
    loginFormData.append("name", props.plant.name)
    loginFormData.append("name", props.plant.name)

    try {
      // make axios post request
      const response = await axios({
        method: "post",
        url: "/api/login",
        data: loginFormData,
        headers: { "Content-Type": "multipart/form-data" },
      });
    } catch(error) {
      console.log(error)
    }
  }

  const handleChange = (e) => {
    props.setplant({
      [e.target.img]: e.target.value,
      [e.target.name]: e.target.value,
      waterInterval: 21600,
      lastWatered: new Date(),
      ...plants
    });
  }



  return (
    <seciton className="form">
      <form
        className="form-inline"
        method="POST"
        action="/api/plant"
        onChange={(e) => e.preventDefault}
      >
        <div className="form-group mb-2">
          <label for="staticEmail2" class="sr-only">
            Image Adress
          </label>
          <input
            type="text"
            name="img"
            value={props.plants.img}
            onChange={handleChange}
            class="form-control"
            id="text"
            placeholder="Image Adress"
          />
        </div>
        <div className="form-group mx-sm-3 mb-2">
          <label for="inputPassword2" class="sr-only">
            Password
          </label>
          <input
            type="password"
            name="name"
            value={props.plants.value}
            onChange={handleChange}
            class="form-control"
            id="inputPassword2"
            placeholder="Password"
          />
        </div>
        <button type="submit" class="btn btn-dark mb-2">
          Add Plant
        </button>
      </form>
    </seciton>
  );
}
