// Imports
import React, { useState, useEffect } from "react";
import { Route } from "react-router";
import Header from "./components/Header.js";
import Plants from "./components/Plants.js";
import Form from "./components/Form.js";
import axios from "axios";

import "./custom.css";
import { getAllPlants } from "./helper/helper.js";

// App
export default function App() {
  const [plants, setPlants] = useState([]);

  useEffect(() => {
    axios.get("/api/plants").then((res) => {
      setPlants(res.data);
    });
    return () => {
      "Searching";
    };
  }, []);

  // Delete plant

  // Plant button
  function wellWatered(waterInterval, lastWatered) {
    const today = new Date();
    const todaysDate = new Date(lastWatered + "Z");
    const diffInSec = today.getTime() - todaysDate.getTime();
    return diffInSec / 1000 > waterInterval;
  }

  const waterPlant = function (plants, id) {

    const day = new Date();
    const dayAndTimeNow = day.toISOString().split("Z")[0];
    const prevDate = plants.lastWatered ;

    const updatedPlant = { ...plants.find((plant) => plant.id === id) };
    const updatedIndex = plants.findIndex((plant) => plant.id === id);
    
  
    console.log(prevDate, "This ONE")

    updatedPlant.lastWatered = dayAndTimeNow
    plants[updatedIndex] = updatedPlant;
    setPlants([...plants]);
    console.log(plants);
  };

  // Water all plants
  const waterAllPlants = function (plants) {
    const waterPlants = plants.map((plant) => {
      const id = plant.id;
      return waterPlant(plants, id);
    });
  };

  // Delete plant
  const deletePlant = function (id) {
    if (window.confirm("Are you sure you want to delete this plant?")) {
      axios.delete(`/api/plants/${id}.json`).then((res) => {
        console.log(res.body);
        setPlants([...plants]);
      });
    }
  };

  return (
    <div>
      <section className="nav">
        <Header key={1} />
      </section>
      <section className="formSection">
        <h4>Add Plant</h4>
        <Form key={2} plants={plants} setPlants={setPlants} />
      </section>
      <section className="plantTable">
        <Plants
          key={3}
          plants={plants}
          waterPlant={waterPlant}
          waterAllPlants={waterAllPlants}
          deletePlant={deletePlant}
        />
      </section>
    </div>
  );
}
