import helperFunction from "../helper/helper";

// Plant componet
export default function Plants(props) {

  [ plantList ] = helperFunction;

  return (
    <table>
      <thead>
        <tr>
          <th>Plants</th>
          <th>Plant Name</th>
          <th>Last Watered</th>
          <th>Remove Plant</th>
          <th className="waterplants-button">
            <button
              type="button"
              className="btn btn-dark"
              onClick={() => {
                props.waterAllPlants(props.plants);
              }}
            >
              Water All Plants üòç
            </button>
          </th>
        </tr>
      </thead>
      <tbody>{plantList}</tbody>
    </table>
  );
}
