// Imports
import React, { Component } from "react";
import { Route } from "react-router";
import Header from "./components/Header.js";
import Plants from "./components/Plants.js";

import "./custom.css";

// App
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      plants: [],
      setPlants: []
    };
  }

  componentDidMount() {
    fetch("/api/plants")
      .then((res) => res.json())
      .then((plants) => {
        console.log(plants);
        this.setState({ plants: plants });
      });
  }

  waterPlant(id) {
    const updatePlants = this.state.plants.map((plant) => {
      if (plant[id]) {
        plant[lastWatered] = new Date();
      }
    });
  }

  render() {
    return (
      <div>
        <section className="nav">
          <Header />
        </section>
        <section className="plantTable">
          <Plants plants={this.state.plants} waterPlant={waterPlant} />
        </section>
      </div>
    );
  }
}
